<?php if (!defined('BASEPATH')) exit('No direct script access allowed'); class Sendmail{        function __construct()    {    }    function send($mailto, $subject, $body)     {           $CI = get_instance();        $CI->load->library('email');        $config['protocol'] = "smtp";        $config['smtp_host'] = "192.168.40.16";        $config['smtp_port'] = "25";        $config['smtp_user'] = "no-reply";         $config['smtp_pass'] = "ozziewong";        $config['charset'] = "utf-8";        $config['mailtype'] = "html";        $config['newline'] = "\r\n";        $CI->email->initialize($config);        $CI->email->from('noreply@innoap.com', 'Sys Admin');        $CI->email->to($mailto);        $CI->email->reply_to('noreply@innoap.com', 'No Reply');        $CI->email->subject($subject);        $CI->email->message($body);        $CI->email->send();    }}